import React, { useState } from "react";
import "./Login.css";

function Login() {
    const [formData, setFormData] = useState({ email: "", password: "" });
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        try {
            const response = await fetch("http://localhost:5001/api/login", { // Replace with your API endpoint
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Login failed");
            }

            // Handle successful login (e.g., redirect, store token)
            const data = await response.json();
            console.log("Login successful:", data);
            // Example: Store token in local storage and redirect
            localStorage.setItem('token', data.token);
            window.location.href = '/dashboard'; // Redirect to dashboard
        } catch (err) {
            setError(err.message);
            console.error("Login error:", err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="login-page">
            {/* ... (Header and other JSX remains the same) */}
            <div className="login-container">
                {/* ... (Breadcrumb remains the same) */}
                <div className="login-content">
                    <h1 className="login-title">Login</h1>
                    {error && <div className="error-message">{error}</div>} {/* Display error message */}
                    <form onSubmit={handleSubmit} className="login-form">
                        {/* ... (Form inputs remain the same) */}
                        <button type="submit" className="login-button" disabled={loading}>
                            {loading ? "Logging in..." : "SIGN IN"} {/* Show loading message */}
                        </button>
                    </form>
                    {/* ... (Footer remains the same) */}
                </div>
                {/* ... (Rewards button remains the same) */}
            </div>
        </div>
    );
}

export default Login;
