import React, { useState } from 'react';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import './Header.css';

function Header() {
  const [showSubMenu, setShowSubMenu] = useState(false);

  // Toggle the secondary dropdown
  const handleShopClick = () => {
    console.log('Before toggle:', showSubMenu);
    setShowSubMenu(!showSubMenu);
    console.log('After toggle:', !showSubMenu);
  };

  return (
    <header className="bg-white border-bottom shadow-sm">
      <Navbar expand="lg" className="py-3">
        <Container>
          {/* Logo */}
          <Navbar.Brand href="#home">
            <img
              src="logo-placeholder.png" // Replace with actual logo path
              alt="Salada Logo"
              className="logo"
            />
          </Navbar.Brand>

          {/* Right Section */}
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mx-auto">
              {/* Main Navigation Links */}
              <Nav.Link href="#shop" className="fw-bold text-dark" onClick={handleShopClick}>
                Shop
              </Nav.Link>
              <Nav.Link href="#blog" className="fw-bold text-dark">Blog</Nav.Link>
              <Nav.Link href="#about" className="fw-bold text-dark">About Us</Nav.Link>
              <Nav.Link href="#rewards" className="fw-bold text-dark">Rewards</Nav.Link>
              <Nav.Link href="#contact" className="fw-bold text-dark">Contact</Nav.Link>
              <Nav.Link href="#find" className="fw-bold text-dark">Find In Store</Nav.Link>
            </Nav>

            {/* Account and Cart */}
            <Nav className="ms-auto">
              <Nav.Link href="#account" className="fw-bold text-dark">Account</Nav.Link>
              <Nav.Link href="#cart" className="fw-bold text-dark">Cart (0)</Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {/* Secondary Dropdown for "Shop" */}
      {showSubMenu && console.log("Dropdown is visible!")}
{showSubMenu && (
  <div className="secondary-dropdown bg-white shadow-sm">
    ...
  </div>
)}
    </header>
  );
}

export default Header;
