import React, { useState } from "react";
import "./Login.css";

function Login() {
  const [formData, setFormData] = useState({ email: "", password: "" });
  const [error, setError] = useState(null); // State variable for error message
  const [loading, setLoading] = useState(false); // St

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted:", formData);
    // Add your login logic here (API call, validation, etc.)
    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(null);
        setLoading(true);

        try {
            const response = await fetch("http://localhost:5001/api/login", { // Replace with your API endpoint
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify(formData),
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(errorData.message || "Login failed");
            }

            // Handle successful login (e.g., redirect, store token)
            const data = await response.json();
            console.log("Login successful:", data);
            // Example: Store token in local storage and redirect
            localStorage.setItem('token', data.token);
            window.location.href = '/dashboard'; // Redirect to dashboard
        } catch (err) {
            setError(err.message);
            console.error("Login error:", err);
        } finally {
            setLoading(false);
        }
    };
  };

  return (
    <div className="login-container">
      <nav className="breadcrumb">
        <a href="/">Home</a> &gt; <span>Account</span>
      </nav>
      <div className="login-content">
        <h1 className="login-title">Login</h1>
        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              required
            />
            <a href="/forgot-password" className="forgot-password-link">
              Forgot your password?
            </a>
          </div>
          <button type="submit" className="login-button">
            Sign In
          </button>
        </form>
        <div className="login-footer">
          <a href="/register">Create account</a> or <a href="/">Return to Store</a>
        </div>
      </div>
    </div>
  );
}

export default Login;
