import React from 'react';
import './HeroSection.css';

function HeroSection() {
  // Sample product details
  const products = [
    {
      id: 1,
      image: 'path-to-black-tea-image.png',
      title: 'Salada Original Blend - Black Tea',
      description: 'A premium blend of orange pekoe and cut black tea.',
    },
    {
      id: 2,
      image: 'path-to-green-tea-image.png',
      title: 'Salada Green Tea for Iced Tea',
      description: 'Refreshing green tea for your iced tea cravings.',
    },
    {
      id: 3,
      image: 'path-to-classic-green-tea-image.png',
      title: 'Salada Green Tea - Classic Green',
      description: 'Classic green tea for a healthy lifestyle.',
    },
    {
      id: 4,
      image: 'path-to-matcha-green-tea-image.png',
      title: 'Salada Matcha Green Tea Blend',
      description: 'A blend of matcha and green tea for a balanced flavor.',
    },
  ];

  return (
    <>
    <section className="text-center bg-light py-5">
      <div className="container">
        {/* Hero Section */}
        <img
          src="hero-image-placeholder.png"
          alt="Teapot and Tea"
          className="img-fluid mb-4"
        />
        <h1 className="text-primary">Take on the day with</h1>
        <h2 className="text-danger fw-bold">SALADA'S REFRESHING TEAS!</h2>
        <button className="btn btn-outline-primary mt-3">Shop Now</button>

        {/* Featured Products Section */}
        <h3 className="mt-5 fw-bold">Featured Products</h3>
        <div className="row mt-4">
          {products.map((product) => (
            <div className="col-md-3 mb-4" key={product.id}>
              <div className="product-card position-relative">
                <img
                  src={product.image}
                  alt={product.title}
                  className="img-fluid product-image"
                />
                <div className="product-details position-absolute text-center">
                  <h5 className="text-dark">{product.title}</h5>
                  <p className="text-muted">{product.description}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
    <h1>Product List</h1>
      {products.map((product) => (
        <div key={product._id}>
          <h2>{product.name}</h2>
          <p>{product.description}</p>
          <p>Price: ${product.price}</p>
          <img src={product.image} alt={product.name} />
        </div>
      ))}
       <section className="text-center my-5">
      <h1 className="keep-it-flowing-text">KEEP IT FLOWING!</h1>
      <p className="subscribe-text">SUBSCRIBE TO ANY PRODUCTS</p>
    </section>
    </>
  );
}

export default HeroSection;
