// Header.js
import React, { useState } from "react";
import { Navbar, Nav, Container } from "react-bootstrap";
import RegisterForm from "./RegisterPage";  // Import the RegisterForm
import "./Header.css";

function Header() {
  const [isAccountHovered, setIsAccountHovered] = useState(false);
  const [showRegisterForm, setShowRegisterForm] = useState(false);

  const handleRegisterClick = () => {
    setShowRegisterForm(true); // Show the registration form
  };

  const handleCloseRegisterForm = () => {
    setShowRegisterForm(false); // Close the registration form
  };

  return (
    <>
      {/* Primary Navigation */}
      <header className="bg-white border-bottom shadow-sm">
        <Navbar expand="lg" className="py-3">
          <Container>
            {/* Logo */}
            <Navbar.Brand href="#home">
              <img
                src="logo-placeholder.png" // Replace with your logo path
                alt="Salada Logo"
                className="logo"
              />
            </Navbar.Brand>

            {/* Navbar Links */}
            <Navbar.Toggle aria-controls="basic-navbar-nav" />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mx-auto">
                <Nav.Link href="#shop" className="fw-bold text-dark">
                  Shop
                </Nav.Link>
                <Nav.Link href="#blog" className="fw-bold text-dark">
                  Blog
                </Nav.Link>
                <Nav.Link href="#about" className="fw-bold text-dark">
                  About Us
                </Nav.Link>
                <Nav.Link href="#rewards" className="fw-bold text-dark">
                  Rewards
                </Nav.Link>
                <Nav.Link href="#contact" className="fw-bold text-dark">
                  Contact
                </Nav.Link>
                <Nav.Link href="#find" className="fw-bold text-dark">
                  Find In Store
                </Nav.Link>
              </Nav>

              {/* Account and Cart */}
              <Nav className="ms-auto">
                <div
                  className="account-dropdown"
                  onMouseEnter={() => setIsAccountHovered(true)}
                  onMouseLeave={() => setIsAccountHovered(false)}
                >
                  <Nav.Link href="#account" className="fw-bold text-dark">
                    Account
                  </Nav.Link>
                  {isAccountHovered && (
                    <div className="dropdown-menu">
                      <a href="#login" className="dropdown-item">
                        Log in
                      </a>
                      <a
                        href="#register"
                        className="dropdown-item"
                        onClick={handleRegisterClick}  // Trigger the registration form
                      >
                        Register
                      </a>
                    </div>
                  )}
                </div>
                <Nav.Link href="#cart" className="fw-bold text-dark">
                  Cart (0)
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
      </header>

      {/* Show the registration form if it's set to true */}
      {showRegisterForm && <RegisterForm onClose={handleCloseRegisterForm} />}
    </>
  );
}

export default Header;
